filter_columns.javascript:
let groupName = 
      data['NIM_Groups'] ? 'NIM_' + data['NIM_Groups']['Name'] : 
      data['AD_Groups']  ? 'AD_' + data['AD_Groups']['cn'] : 
      data['G_Groups'] ? 'Google_' + data['G_Groups']['name'] :
      'Unknown_';

// Replace Spaces with Underscores
groupName = groupName.replace(/\W/g,'_');

// Return Role Name
return 'RoleGroupOverride-' + groupName;

// Needed for NIM Group Lookups, because Lookups don't support NUMBER data types.
return data['G_Groups'] ? data['G_Groups']['name'] :
  data['AD_Groups'] ? data['AD_Groups']['cn'] :
  data['NIM_Groups'] ? data['NIM_Groups']['Name'] : 
  'Invalid Name';

